  --shadow-sm: 0px 2px 0px 0px hsl(220 10% 88% / 0.10), 0px 1px 2px -1px hsl(220 10% 88% / 0.15);
  --shadow: 0px 2px 0px 0px hsl(220 10% 88% / 0.10), 0px 1px 2px -1px hsl(220 10% 88% / 0.15);
