import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertCampaignSchema, type InsertCampaign, type Program } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";

interface CampaignFormProps {
  onSubmit: (data: InsertCampaign) => void;
  initialData?: Partial<InsertCampaign>;
  isEditing?: boolean;
}

export function CampaignForm({ onSubmit, initialData, isEditing = false }: CampaignFormProps) {
  // Fetch programs data
  const { data: programs = [], isLoading: programsLoading, error: programsError } = useQuery({
    queryKey: ['/api/programs'],
    queryFn: async () => {
      const response = await fetch('/api/programs');
      if (!response.ok) {
        throw new Error('Failed to fetch programs');
      }
      return response.json() as Promise<Program[]>;
    }
  });

  // Extract unique values for dropdowns
  const uniqueFacultades = Array.from(new Set(programs.map(p => p.facultad).filter((f): f is string => f !== null && f !== undefined))).sort();
  const uniqueModalidades = Array.from(new Set(programs.map(p => p.modalidad).filter((m): m is string => m !== null && m !== undefined))).sort();
  const uniqueLineas = Array.from(new Set(programs.map(p => p.linea_negocio).filter((l): l is string => l !== null && l !== undefined))).sort();

  // Predefined options that don't come from programs
  const mediosOptions = [
    "Google Ads",
    "Facebook Ads", 
    "Instagram Ads",
    "LinkedIn Ads",
    "YouTube Ads",
    "Twitter Ads",
    "Email Marketing",
    "Display",
    "Programmatic"
  ];

  const tiposCampanaOptions = [
    "Conversión",
    "Reconocimiento",
    "Tráfico",
    "Generación de leads",
    "Retargeting",
    "Brand Awareness"
  ];

  const form = useForm<InsertCampaign>({
    resolver: zodResolver(insertCampaignSchema),
    defaultValues: {
      medio: initialData?.medio || "",
      programa_interes: initialData?.programa_interes || "",
      tipo_campana: initialData?.tipo_campana || "",
      nombre_campania: initialData?.nombre_campania || "",
      nomenclatura_programa: initialData?.nomenclatura_programa || "",
      modalidad_estudio: initialData?.modalidad_estudio || "",
      linea: initialData?.linea || "",
      facultad: initialData?.facultad || "",
      nomenclatura_modalidad: initialData?.nomenclatura_modalidad || "",
      nomenclatura_linea: initialData?.nomenclatura_linea || "",
      nomenclatura_tipo_campana: initialData?.nomenclatura_tipo_campana || "",
      nomenclatura_modalidad3: initialData?.nomenclatura_modalidad3 || "",
      nombre_sf: initialData?.nombre_sf || "",
      url_programa: initialData?.url_programa || "",
      lista_pardot: initialData?.lista_pardot || "",
    },
  });

  const handleSubmit = (data: InsertCampaign) => {
    onSubmit(data);
    console.log('Campaign form submitted:', data);
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>{isEditing ? 'Editar Campaña' : 'Nueva Campaña'}</CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label>Medio *</Label>
              <Select onValueChange={(value) => form.setValue("medio", value)} value={form.watch("medio") || ""}>
                <SelectTrigger data-testid="select-medio">
                  <SelectValue placeholder="Seleccionar medio..." />
                </SelectTrigger>
                <SelectContent>
                  {mediosOptions.map((medio) => (
                    <SelectItem key={medio} value={medio}>
                      {medio}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {form.formState.errors.medio && (
                <p className="text-sm text-destructive">{form.formState.errors.medio.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label>Programa de Interés *</Label>
              {programsLoading ? (
                <Skeleton className="h-10 w-full" />
              ) : programsError ? (
                <div className="text-sm text-destructive">Error cargando programas</div>
              ) : (
                <Select onValueChange={(value) => form.setValue("programa_interes", value)} value={form.watch("programa_interes") || ""}>
                  <SelectTrigger data-testid="select-programa-interes">
                    <SelectValue placeholder="Seleccionar programa..." />
                  </SelectTrigger>
                  <SelectContent>
                    {programs.filter(p => p.nombre_programa).map((programa) => (
                      <SelectItem key={programa.id} value={programa.nombre_programa!}>
                        {programa.nombre_programa}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              )}
              {form.formState.errors.programa_interes && (
                <p className="text-sm text-destructive">{form.formState.errors.programa_interes.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label>Tipo de Campaña *</Label>
              <Select onValueChange={(value) => form.setValue("tipo_campana", value)} value={form.watch("tipo_campana") || ""}>
                <SelectTrigger data-testid="select-tipo-campana">
                  <SelectValue placeholder="Seleccionar tipo..." />
                </SelectTrigger>
                <SelectContent>
                  {tiposCampanaOptions.map((tipo) => (
                    <SelectItem key={tipo} value={tipo}>
                      {tipo}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {form.formState.errors.tipo_campana && (
                <p className="text-sm text-destructive">{form.formState.errors.tipo_campana.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="nombre_campania">Nombre de Campaña *</Label>
              <Input
                id="nombre_campania"
                {...form.register("nombre_campania")}
                data-testid="input-nombre-campania"
                placeholder="Nombre descriptivo de la campaña"
              />
              {form.formState.errors.nombre_campania && (
                <p className="text-sm text-destructive">{form.formState.errors.nombre_campania.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label>Modalidad de Estudio</Label>
              {programsLoading ? (
                <Skeleton className="h-10 w-full" />
              ) : (
                <Select onValueChange={(value) => form.setValue("modalidad_estudio", value)} value={form.watch("modalidad_estudio") || ""}>
                  <SelectTrigger data-testid="select-modalidad-estudio">
                    <SelectValue placeholder="Seleccionar modalidad..." />
                  </SelectTrigger>
                  <SelectContent>
                    {uniqueModalidades.map((modalidad) => (
                      <SelectItem key={modalidad} value={modalidad}>
                        {modalidad}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              )}
            </div>

            <div className="space-y-2">
              <Label>Facultad</Label>
              {programsLoading ? (
                <Skeleton className="h-10 w-full" />
              ) : (
                <Select onValueChange={(value) => form.setValue("facultad", value)} value={form.watch("facultad") || ""}>
                  <SelectTrigger data-testid="select-facultad">
                    <SelectValue placeholder="Seleccionar facultad..." />
                  </SelectTrigger>
                  <SelectContent>
                    {uniqueFacultades.map((facultad) => (
                      <SelectItem key={facultad} value={facultad}>
                        {facultad}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              )}
            </div>

            <div className="space-y-2">
              <Label>Línea</Label>
              {programsLoading ? (
                <Skeleton className="h-10 w-full" />
              ) : (
                <Select onValueChange={(value) => form.setValue("linea", value)} value={form.watch("linea") || ""}>
                  <SelectTrigger data-testid="select-linea">
                    <SelectValue placeholder="Seleccionar línea..." />
                  </SelectTrigger>
                  <SelectContent>
                    {uniqueLineas.map((linea) => (
                      <SelectItem key={linea} value={linea}>
                        {linea}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="nombre_sf">Nombre SF</Label>
              <Input
                id="nombre_sf"
                {...form.register("nombre_sf")}
                data-testid="input-nombre-sf"
                placeholder="Nombre en Salesforce"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="url_programa">URL Programa</Label>
              <Input
                id="url_programa"
                {...form.register("url_programa")}
                data-testid="input-url-programa"
                placeholder="https://..."
                type="url"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="lista_pardot">Lista Pardot</Label>
              <Input
                id="lista_pardot"
                {...form.register("lista_pardot")}
                data-testid="input-lista-pardot"
                placeholder="ID de lista Pardot"
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="nomenclatura_programa">Nomenclatura Programa</Label>
              <Textarea
                id="nomenclatura_programa"
                {...form.register("nomenclatura_programa")}
                data-testid="input-nomenclatura-programa"
                placeholder="Detalles de nomenclatura..."
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="nomenclatura_modalidad">Nomenclatura Modalidad</Label>
              <Textarea
                id="nomenclatura_modalidad"
                {...form.register("nomenclatura_modalidad")}
                data-testid="input-nomenclatura-modalidad"
                placeholder="Detalles de nomenclatura..."
              />
            </div>
          </div>

          <div className="flex gap-4">
            <Button 
              type="submit" 
              data-testid="button-submit-campaign"
              className="hover-elevate active-elevate-2"
            >
              {isEditing ? 'Actualizar' : 'Crear'} Campaña
            </Button>
            <Button 
              type="button" 
              variant="outline" 
              data-testid="button-cancel-campaign"
              onClick={() => console.log('Form cancelled')}
              className="hover-elevate active-elevate-2"
            >
              Cancelar
            </Button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/CompleteView.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  Accordion, 
  AccordionContent, 
  AccordionItem, 
  AccordionTrigger 
} from "@/components/ui/accordion";
import { Download, ExternalLink } from "lucide-react";

interface CompleteViewData {
  campaigns: Array<{
    id: number;
    nombre_campania: string;
    medio: string;
    programa_interes: string;
    facultad: string;
    tipo_campana: string;
    linea: string;
    modalidad_estudio: string;
    lista_pardot: string;
    url_programa: string;
    adGroups: Array<{
      id: number;
      nombre_grupo: string;
      numero_grupo: number;
      fsa: string;
      cohorte: string;
      tipo_publico: string;
      ads: Array<{
        id: number;
        nombre_anuncio: string;
        tipo_anuncio: string;
        nomenclatura_pardot: string;
      }>;
    }>;
  }>;
}

interface CompleteViewProps {
  data: CompleteViewData;
  onExportCSV: () => void;
  loading?: boolean;
}

export function CompleteView({ data, onExportCSV, loading = false }: CompleteViewProps) {
  if (loading) {
    return (
      <Card>
        <CardContent className="flex items-center justify-center py-12">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        </CardContent>
      </Card>
    );
  }

  const totalAdGroups = data.campaigns.reduce((sum, campaign) => sum + campaign.adGroups.length, 0);
  const totalAds = data.campaigns.reduce(
    (sum, campaign) => sum + campaign.adGroups.reduce((adSum, group) => adSum + group.ads.length, 0), 
    0
  );

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <div>
            <CardTitle>Vista Completa - Estructura Jerárquica</CardTitle>
            <div className="flex gap-4 mt-2">
              <Badge variant="outline" data-testid="text-campaigns-count">
                {data.campaigns.length} Campañas
              </Badge>
              <Badge variant="outline" data-testid="text-ad-groups-count">
                {totalAdGroups} Grupos
              </Badge>
              <Badge variant="outline" data-testid="text-ads-count">
                {totalAds} Anuncios
              </Badge>
            </div>
          </div>
          <Button 
            onClick={() => {
              console.log('Exporting CSV data');
              onExportCSV();
            }}
            data-testid="button-export-csv"
            className="hover-elevate active-elevate-2"
          >
            <Download className="h-4 w-4 mr-2" />
            Exportar CSV
          </Button>
        </CardHeader>
      </Card>

      {data.campaigns.length === 0 ? (
        <Card>
          <CardContent className="text-center py-12 text-muted-foreground">
            No hay datos para mostrar
          </CardContent>
        </Card>
      ) : (
        <Accordion type="multiple" className="space-y-4">
          {data.campaigns.map((campaign) => (
            <AccordionItem 
              key={campaign.id} 
              value={campaign.id.toString()}
              className="border rounded-lg"
              data-testid={`accordion-campaign-${campaign.id}`}
            >
              <AccordionTrigger className="px-6 py-4 hover:no-underline hover-elevate">
                <div className="flex items-center justify-between w-full mr-4">
                  <div className="text-left">
                    <div className="font-semibold">{campaign.nombre_campania}</div>
                    <div className="text-sm text-muted-foreground">
                      {campaign.medio} • {campaign.programa_interes} • {campaign.facultad}
                    </div>
                  </div>
                  <div className="flex gap-2">
                    <Badge variant="secondary">{campaign.tipo_campana}</Badge>
                    <Badge variant="outline">
                      {campaign.adGroups.length} grupos
                    </Badge>
                  </div>
                </div>
              </AccordionTrigger>
              <AccordionContent className="px-6 pb-4">
                <div className="space-y-4">
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                    <div>
                      <span className="font-medium text-muted-foreground">Línea:</span>
                      <div>{campaign.linea || '-'}</div>
                    </div>
                    <div>
                      <span className="font-medium text-muted-foreground">Modalidad:</span>
                      <div>{campaign.modalidad_estudio || '-'}</div>
                    </div>
                    <div>
                      <span className="font-medium text-muted-foreground">Lista Pardot:</span>
                      <div>{campaign.lista_pardot || '-'}</div>
                    </div>
                    <div>
                      <span className="font-medium text-muted-foreground">URL Programa:</span>
                      {campaign.url_programa ? (
                        <a 
                          href={campaign.url_programa} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="text-primary hover:underline inline-flex items-center gap-1"
                        >
                          Ver <ExternalLink className="h-3 w-3" />
                        </a>
                      ) : (
                        <div>-</div>
                      )}
                    </div>
                  </div>

                  {campaign.adGroups.length > 0 && (
                    <div className="space-y-3">
                      <h4 className="font-medium text-foreground">Grupos de Anuncios</h4>
                      {campaign.adGroups.map((group) => (
                        <Card key={group.id} className="bg-muted/30">
                          <CardContent className="p-4">
                            <div className="flex items-center justify-between mb-2">
                              <div className="font-medium">
                                Grupo #{group.numero_grupo}: {group.nombre_grupo}
                              </div>
                              <Badge variant="outline">
                                {group.ads.length} anuncios
                              </Badge>
                            </div>
                            <div className="grid grid-cols-1 md:grid-cols-3 gap-2 text-sm mb-3">
                              <div>
                                <span className="text-muted-foreground">FSA:</span> {group.fsa || '-'}
                              </div>
                              <div>
                                <span className="text-muted-foreground">Cohorte:</span> {group.cohorte || '-'}
                              </div>
                              <div>
                                <span className="text-muted-foreground">Tipo Público:</span> {group.tipo_publico || '-'}
                              </div>
                            </div>

                            {group.ads.length > 0 && (
                              <div className="space-y-2">
                                <div className="text-sm font-medium text-muted-foreground">Anuncios:</div>
                                <div className="space-y-2">
                                  {group.ads.map((ad) => (
                                    <div 
                                      key={ad.id} 
                                      className="bg-card border rounded p-3"
                                      data-testid={`card-ad-${ad.id}`}
                                    >
                                      <div className="flex items-center justify-between">
                                        <div className="font-medium">{ad.nombre_anuncio}</div>
                                        <Badge variant="secondary" className="text-xs">
                                          {ad.tipo_anuncio}
                                        </Badge>
                                      </div>
                                      {ad.nomenclatura_pardot && (
                                        <div className="text-xs text-muted-foreground mt-1">
                                          Pardot: {ad.nomenclatura_pardot}
                                        </div>
                                      )}
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  )}
                </div>
              </AccordionContent>
            </AccordionItem>
          ))}
        </Accordion>
      )}
    </div>
  );
}

client/src/components/DataTable.tsx.content:
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Edit, Trash2, Eye } from "lucide-react";

interface Column {
  key: string;
  label: string;
  render?: (value: any, row: any) => React.ReactNode;
}

interface DataTableProps {
  title: string;
  columns: Column[];
  data: any[];
  onEdit?: (item: any) => void;
  onDelete?: (item: any) => void;
  onView?: (item: any) => void;
  loading?: boolean;
}

export function DataTable({ 
  title, 
  columns, 
  data, 
  onEdit, 
  onDelete, 
  onView,
  loading = false 
}: DataTableProps) {
  if (loading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>{title}</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle>{title}</CardTitle>
        <Badge variant="secondary" data-testid="text-count">
          {data.length} elementos
        </Badge>
      </CardHeader>
      <CardContent>
        {data.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            No hay datos para mostrar
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b">
                  {columns.map((column) => (
                    <th
                      key={column.key}
                      className="text-left py-3 px-4 font-medium text-sm text-muted-foreground"
                    >
                      {column.label}
                    </th>
                  ))}
                  {(onEdit || onDelete || onView) && (
                    <th className="text-right py-3 px-4 font-medium text-sm text-muted-foreground">
                      Acciones
                    </th>
                  )}
                </tr>
              </thead>
              <tbody>
                {data.map((row, index) => (
                  <tr
                    key={row.id || index}
                    className="border-b hover:bg-muted/30 transition-colors"
                    data-testid={`row-item-${row.id || index}`}
                  >
                    {columns.map((column) => (
                      <td key={column.key} className="py-3 px-4 text-sm">
                        {column.render 
                          ? column.render(row[column.key], row)
                          : row[column.key] || '-'
                        }
                      </td>
                    ))}
                    {(onEdit || onDelete || onView) && (
                      <td className="py-3 px-4 text-right">
                        <div className="flex gap-1 justify-end">
                          {onView && (
                            <Button
                              variant="ghost"
                              size="icon"
                              onClick={() => {
                                console.log('Viewing item:', row);
                                onView(row);
                              }}
                              data-testid={`button-view-${row.id || index}`}
                              className="h-8 w-8 hover-elevate"
                            >
                              <Eye className="h-4 w-4" />
                            </Button>
                          )}
                          {onEdit && (
                            <Button
                              variant="ghost"
                              size="icon"
                              onClick={() => {
                                console.log('Editing item:', row);
                                onEdit(row);
                              }}
                              data-testid={`button-edit-${row.id || index}`}
                              className="h-8 w-8 hover-elevate"
                            >
                              <Edit className="h-4 w-4" />
                            </Button>
                          )}
                          {onDelete && (
                            <Button
                              variant="ghost"
                              size="icon"
                              onClick={() => {
                                if (confirm('¿Está seguro de que desea eliminar este elemento?')) {
                                  console.log('Deleting item:', row);
                                  onDelete(row);
                                }
                              }}
                              data-testid={`button-delete-${row.id || index}`}
                              className="h-8 w-8 hover-elevate text-destructive"
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          )}
                        </div>
                      </td>
                    )}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/Navigation.tsx.content:
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Moon, Sun } from "lucide-react";
import { useState, useEffect } from "react";

export function Navigation() {
  const [location] = useLocation();
  const [isDark, setIsDark] = useState(false);

  useEffect(() => {
    const isDarkMode = document.documentElement.classList.contains('dark');
    setIsDark(isDarkMode);
  }, []);

  const toggleTheme = () => {
    const newTheme = !isDark;
    setIsDark(newTheme);
    document.documentElement.classList.toggle('dark', newTheme);
    localStorage.setItem('theme', newTheme ? 'dark' : 'light');
    console.log(`Theme switched to: ${newTheme ? 'dark' : 'light'}`);
  };

  const navItems = [
    { path: "/", label: "Campañas", testId: "nav-campaigns" },
    { path: "/grupos", label: "Grupos de anuncios", testId: "nav-ad-groups" },
    { path: "/anuncios", label: "Anuncios", testId: "nav-ads" },
    { path: "/programas", label: "Programas", testId: "nav-programs" },
    { path: "/completo", label: "Completo", testId: "nav-complete" },
    { path: "/buscar", label: "Buscar", testId: "nav-search" },
  ];

  return (
    <nav className="border-b bg-card">
      <div className="flex h-16 items-center justify-between px-6">
        <div className="flex items-center space-x-8">
          <h1 className="text-xl font-semibold text-foreground">
            Campaign Manager
          </h1>
          <div className="flex space-x-1">
            {navItems.map((item) => (
              <Button
                key={item.path}
                variant={location === item.path ? "secondary" : "ghost"}
                size="sm"
                asChild
                data-testid={item.testId}
                className="hover-elevate"
              >
                <Link href={item.path}>{item.label}</Link>
              </Button>
            ))}
          </div>
        </div>
        <Button
          variant="ghost"
          size="icon"
          onClick={toggleTheme}
          data-testid="button-theme-toggle"
          className="hover-elevate"
        >
          {isDark ? <Sun className="h-4 w-4" /> : <Moon className="h-4 w-4" />}
        </Button>
      </div>
    </nav>
  );
}

